import { useState, useEffect } from 'react'
import GoogleMapReact from 'google-map-react'
import MapMarker from './MapMarker'
import MapInfoBox from './MapInfoBox'

var turkey = {
    lat: 41.015137,
    lng: 28.979530
}
const Map = ({ eventData, center, zoom }) => {
    const [locationInfo, setLocationInfo] = useState({});
    const [trendsInfo, setTrendsInfo] = useState({});

    const markers = eventData.map(item => {
        var lat = parseFloat(item.lat);
        var lng = parseFloat(item.long);
        return <MapMarker lat={turkey.lat} lng={turkey.lng} onClick={() => setLocationInfo(item)} />
    })


    function handleClick(e) {
        var lat = e.lat;
        var lng = e.lng;
        fetch("/api/closest", {
            method: "post",
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                lat: lat,
                lng: lng
            })
        })
            .then(res => res.json())
            .then(function (res) {
                console.log(res)
                const woeid = res[0].woeid;

                fetch("/api/trends", {
                    method: "post",
                    headers: {
                        'Accept': 'application/json',
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify({
                        woeid: woeid
                    })
                })
                    .then(res => res.json())
                    .then(function (response) {
                        var trends = response[0];
                        setTrendsInfo(trends);
                        console.log(trendsInfo)
                    });
            })
    }

    return (
        <div id="map">
            <GoogleMapReact
                bootstrapURLKeys={{ key: 'AIzaSyBKcbWgVYRSdCv0PCn6dCOvgdV7MjcE-R0' }}
                defaultCenter={center}
                defaultZoom={zoom}
                onClick={(e) => handleClick(e)}
            >

            </GoogleMapReact>
            <p className="right">lorem</p>

            { trendsInfo && JSON.stringify(trendsInfo, null, 2)}

            {
                products.map(function (item, index) {
                    // don't show archived products
                    if (!item.active) return;

                    var price = getProductPrice(item.id);
                    var id = item.id;
                    var priceId = getProductPriceId(id);
                    return <div key={id} className="col-lg-3 col-sm-6 mb-3">
                        <div className="card h-100" id={id}><img className="card-img-top" loading="lazy" src={item.images[0]} />
                            <div className="card-body d-flex flex-column">
                                <h5 className="card-title">{item.name}</h5>
                                <p className="card-text">$<span>{price}</span></p>
                                <div className="d-flex mt-auto">
                                    <input className="form-control mr-2" type="number" min="1" onChange={(event) => setQuantity(event.target.value)} value={quantity} placeholder="1" />
                                    <button onClick={() => goCheckout(quantity, priceId)} className="w-100 align-self-start btn btn-outline-primary">Buy</button>
                                </div>
                            </div>
                        </div>
                    </div>;
                })
            }


            {locationInfo && <MapInfoBox info={locationInfo} />}
        </div>
    )
}

Map.defaultProps = {
    center: {
        lat: 42.3265,
        lng: -122.8756
    },
    zoom: 1
}

export default Map
